/* ------------------------------- Data ------------------------------ */

type IconLike = (props: IconProps) => ReactElement;

type NavItem = {
  href: string;
  label: string;
  icon: IconLike;
  /** Optional custom match logic for active state */
  match?: (pathname: string) => boolean;
};

type SidebarVariant = "dashboard" | "organization";

const dashboardItems: NavItem[] = [
  { href: "/dashboard", label: "Dashboard", icon: DashboardGridIcon },
  { href: "/dashboard/events", label: "Events", icon: DashboardEventsIcon },
  {
    href: "/dashboard/connections",
    label: "Connections",
    icon: ConnectionsIcon,
  },
  { href: "/dashboard/finances", label: "Finances", icon: FinancesIcon },
  { href: "/dashboard/data", label: "Data", icon: DataIcon },
];

/**
 * Organization dashboard nav â€“ hrefs are *relative segments* which we
 * will turn into absolute URLs with the current org id.
 */
const orgItems: NavItem[] = [
  {
    href: ".", // will become /dashboard/organizations/:id
    label: "Home",
    icon: OrgHomeIcon,
    match: (pathname) => {
      const sections = ["/events", "/edit", "/team", "/finance", "/settings"];
      return !sections.some(
        (seg) => pathname.endsWith(seg) || pathname.includes(`${seg}/`)
      );
    },
  },
  {
    href: "events",
    label: "Events",
    icon: OrgEventsIcon,
    match: (pathname) => pathname.includes("/events"),
  },
  {
    href: "edit",
    label: "Edit",
    icon: OrgEditIcon,
    match: (pathname) => pathname.includes("/edit"),
  },
  {
    href: "team",
    label: "Team",
    icon: OrgTeamIcon,
    match: (pathname) => pathname.includes("/team"),
  },
  {
    href: "finances",
    label: "Finance",
    icon: OrgFinanceIcon,
    match: (pathname) => pathname.includes("/finance"),
  },
];

/* ----------------------------- Component --------------------------- */

type SidebarProps = {
  variant?: SidebarVariant;
};

export default function Sidebar({ variant = "dashboard" }: SidebarProps) {
  const pathname = usePathname();
  const isOrg = variant === "organization";
  const items = isOrg ? orgItems : dashboardItems;

  // Figure out /dashboard/organizations/:id base from the current URL
  let orgBase: string | null = null;
  if (isOrg && pathname) {
    const match = pathname.match(/^\/dashboard\/organizations\/([^\/]+)/);
    if (match) {
      orgBase = `/dashboard/organizations/${match[1]}`;
    }
  }

  return (
    <nav className="flex h-full w-[248px] flex-col border-r border-white/5 bg-[#0F1117] px-6 py-8">
      {/* Logo for org dashboard sidebar */}
      {isOrg && (
        <div className="mb-8">
          <Link href="/dashboard" className="flex items-center">
            <Image
              src="/Logo.svg"
              alt="Tikd."
              width={72}
              height={24}
              priority
            />
          </Link>
        </div>
      )}

      {/* Create event button for organization sidebar */}
      {isOrg && (
        <div className="mb-8">
          <Link
            href={orgBase ? `${orgBase}/events/create` : "/dashboard/events"}
            className="flex h-9 w-full items-center justify-center rounded-full bg-neutral-0 font-semibold text-neutral-950 transition hover:bg-primary-500"
          >
            Create event <span className="ml-2 text-lg leading-none">+</span>
          </Link>
        </div>
      )}

      <ul className="space-y-2">
        {items.map(({ href, label, icon: Icon, match }) => {
          // Build absolute href for org links using orgBase
          const computedHref =
            isOrg && orgBase
              ? href === "." || href === ""
                ? orgBase
                : href.startsWith("/")
                  ? href
                  : `${orgBase}/${href}`
              : href;

          const active = match ? match(pathname) : pathname === computedHref;

          return (
            <li key={`${variant}-${label}`}>
              <Link
                href={computedHref}
                className={clsx(
                  "flex items-center gap-4 rounded-lg px-4 py-3 font-semibold transition-colors",
                  "hover:bg-neutral-800 hover:text-white",
                  active ? "bg-neutral-800 text-white" : "text-neutral-200"
                )}
              >
                <Icon className="h-5 w-5 shrink-0 text-neutral-500" />
                <span>{label}</span>
              </Link>
            </li>
          );
        })}
      </ul>

      {/* Bottom Settings link */}
      <div className="mt-auto hidden md:block">
        <Link
          href={
            isOrg && orgBase ? `${orgBase}/settings` : "/dashboard/settings"
          }
          className={clsx(
            "mt-10 flex items-center gap-4 rounded-lg px-4 py-3 font-semibold transition-colors",
            "hover:bg-neutral-800 hover:text-white",
            pathname.includes("/settings")
              ? "bg-neutral-800 text-white"
              : "text-neutral-200"
          )}
        >
          <SettingsCogIcon className="h-5 w-5 shrink-0 text-neutral-500" />
          <span>Settings</span>
        </Link>
      </div>
    </nav>
  );
}
